import React from "react";
import { createGlobalStyle } from "styled-components";
import { ThemeProvider } from "styled-components";
import styled from "styled-components";
import theme from 'styled-theming';

createGlobalStyle`
  @font-face {
    font-family: 'corbel';
    src: url('../../Resources/corbel.ttf');
  }
  @font-face {
    font-family: 'kartika';
    src: url('../../Resources/kartika.ttf');
  }
`;

const Background = theme('mode', {
    tema1_2: '#26273d',
    tema1_11: '#E6E8F5',
  });
  const H1_Color = theme('mode', {
    tema1_2: '#a3a7b0',
    tema1_11: '#7B678B',
  });
  const Input_Background = theme('mode', {
    tema1_2: '#26273d',
    tema1_11: '#FFFFFF',
  });
  const Input_LoginColor = theme('mode', {
    tema1_2: '#46B38C',
    tema1_11: '#46B38C',
  });

const Tema1_2_v2 = styled.div`
  background: ${Background}!important;  
  min-height: 100vh;
  h1{
    color: ${H1_Color};
    font-size: 30px;
    font-family: corbel;
    font-weight: 800;
    text-align: center;
  }
  input {
    background-color: ${Input_Background} !important;
    border-color: #a3a7b0 !important;
    border: 2px solid #a3a7b0 !important;
    border-radius: 2px !important;
    font-weight: 800;
    color: #a3a7b0;
  }
  input[type="text"]:focus {
    background-color: ${Input_Background} !important;
    border-color: #a3a7b0 !important;
    border: 2px solid #a3a7b0 !important;
    border-radius: 2px !important;
    font-weight: 800;
    color: #a3a7b0;
  }
  input[type="text"]:hover {
    background-color: ${Input_Background} !important;
    border-color: #a3a7b0 !important;
    border: 2px solid #a3a7b0 !important;
    border-radius: 2px !important;
    font-weight: 800;
    color: #a3a7b0;
  }
  input[type="text"]:focus:hover {
    background-color: ${Input_Background} !important;
    border-color: #a3a7b0 !important;
    border: 2px solid #a3a7b0 !important;
    border-radius: 2px !important;
    font-weight: 800;
    color: #a3a7b0;
  }
  input[type="text"]:blur {
    background-color: ${Input_Background} !important;
    border-color: #a3a7b0 !important;
    border: 2px solid #a3a7b0 !important;
    border-radius: 2px !important;
    font-weight: 800;
    color: #a3a7b0;
  }

  .btnLogin{
    background-color: #46B38C
    border-color: #46B38C
    width: 100%
    height: 40px  
    color: #DFDFDF
    font-family: corbel
    font-weight: 800
    font-size: 18px
    border-width: thin
  }
  .btnLogin:onclick {
    width: 70px;
    height: 74px;
  }
`;
  
  const Theme1 = ({ children, modeID }) => (              
    <ThemeProvider theme={{mode: modeID}}>
      <Tema1_2_v2>{children}</Tema1_2_v2>
    </ThemeProvider>
  );
  
  export {Theme1};
