import React from "react";
import { createGlobalStyle } from "styled-components";
import { ThemeProvider } from "styled-components";
import styled from "styled-components";
import theme from 'styled-theming';

createGlobalStyle`
  @font-face {
    font-family: 'corbel';
    src: url('../../Resources/corbel.ttf');
  }
  @font-face {
    font-family: 'kartika';
    src: url('../../Resources/kartika.ttf');
  }
`;

const boxBackgroundColor = theme('mode', {
    light: 'blue',
    dark: 'red',
  });

const Tema1_2_v2 = styled.div`
  background: ${boxBackgroundColor}!important;
  min-height: 100%!important;
  min-height: 100vh;
`;
  
  const Theme = ({ children, modeID }) => (  
    console.log("---------------------------"  + boxBackgroundColor),
    <ThemeProvider theme={{mode: modeID}}>
      <Tema1_2_v2>{children}</Tema1_2_v2>
    </ThemeProvider>
  );
  
  export {Tema1_2_v2};
