import React, {Component}  from 'react'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import { Container, Row, Col} from 'reactstrap'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import {postlogin} from '../actions/index';

class Login extends Component{

    constructor(props){
        super(props);    
        this.PostLoginData = this.PostLoginData.bind(this);        
        this.state = {login: ''};
        this.state = {pass: ''};
        this.state = {errologin: ''};
        this.state = {erropass: ''};
    }
    
    PostLoginData(event){
        event.preventDefault();
        this.props.postlogin(this.state.login, this.state.pass );  
    } 
    
    render(){
        return(
            <div>      
                <Container>
                    <Row>
                        <Col xl={12}>
                            <h1>Login</h1>
                        </Col>            
                    </Row>    
                    <Row>
                        <Col xl={12}>
                            <Form>
                                <Form.Group controlId="formUsuario">                        
                                    <Form.Control type="text" placeholder="UsuÃ¡rio" onChange={evt => this.updateInputValueLogin(evt)}/>                        
                                </Form.Group>
                                <div className="invalid-feedback d-block">
                                    
                                </div>
                                <Form.Group controlId="formPassword">                        
                                    <Form.Control type="password" placeholder="Senha" onChange={evt => this.updateInputValuePass(evt)}/>
                                </Form.Group>     
                                <div className="invalid-feedback d-block">
                                    
                                </div>
                                {/* <Button variant="primary" type="button" onClick={this.PostLoginData}> */}
                                <Button variant="primary" type="button" onClick={this.TESTE}>
                                    Entrar
                                </Button>
                            </Form>
                        </Col>            
                    </Row>     
                </Container>
            </div>  
        )
      }
      updateInputValueLogin(evt) {          
        this.setState({
            login: evt.target.value
        });
      }
      updateInputValuePass(evt) {        
        this.setState({
            pass: evt.target.value
        });
      }

      TESTE(evt)
      {
        console.log(evt);
          console.log("TESTE");
      }

}
    
function mapDispatchToProps(dispatch){
    return bindActionCreators({postlogin}, dispatch);
}

export default connect(null, mapDispatchToProps)(Login);
