import React, {Component}  from 'react'
// import PropTypes from 'prop-types'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import { Container, Row, Col} from 'reactstrap'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import {postlogin} from '../actions/index';

class Login extends Component{

    constructor(props){
        super(props);    
        this.PostLoginData = this.PostLoginData.bind(this);        
        this.state = {login: ''};
        this.state = {pass: ''};
    }
    
    PostLoginData(event){
        event.preventDefault();
        this.props.postlogin();  
    } 
    
    render(){
        return(
            <div>      
                <Container>
                    <Row>
                        <Col xl={12}>
                            <h1>Login</h1>
                        </Col>            
                    </Row>    
                    <Row>
                        <Col xl={12}>
                            <Form>
                                <Form.Group controlId="formUsuario">                        
                                    <Form.Control type="text" placeholder="UsuÃ¡rio" value={this.state.login} onChange={evt => this.updateInputValue(evt)}/>                        
                                </Form.Group>
                                <Form.Group controlId="formPassword">                        
                                    <Form.Control type="password" placeholder="Senha" value={this.state.pass} onChange={evt => this.updateInputValue(evt)}/>
                                </Form.Group>                    
                                <Button variant="primary" type="button" onClick={this.PostLoginData}>
                                    Entrar
                                </Button>
                            </Form>
                        </Col>            
                    </Row>     
                </Container>
            </div>  
        )
      }
      updateInputValue(evt) {
        this.setState({
            login: evt.target.value
        });
      }
}
    
function mapDispatchToProps(dispatch){
    return bindActionCreators({postlogin}, dispatch);
}

export default connect(null, mapDispatchToProps)(Login);
