import React, {Component}  from 'react'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import { Container, Row, Col} from 'reactstrap'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import {postlogin} from '../actions/index';

import {Tema1_2, Tema1_2_H1} from '../styles/css/Geral'

class Login extends Component{

    constructor(props){
        super(props);    
        this.PostLoginData = this.PostLoginData.bind(this);        
        this.state = {login: ''};
        this.state = {pass: ''};
        this.state = {errologin: ''};
        this.state = {erropass: ''};
    }    
    render(){
        return(             
            <div>                      
                <Container>
                    <Row>
                        <Col xl={12}>
                            <Tema1_2_H1>                            
                            <h1>Login</h1>                                                            
                            </Tema1_2_H1>
                            
                        </Col>            
                    </Row>    
                    <Row>
                        <Col xl={12}>
                            <Form>
                                <Form.Group controlId="formUsuario">                        
                                    <Form.Control type="text" placeholder="Usu谩rio" onChange={evt => this.updateInputValueLogin(evt)}/>                        
                                    <span className="invalid-feedback d-block">
                                        {this.state.errologin}
                                    </span>
                                </Form.Group>                                
                                <Form.Group controlId="formPassword">                        
                                    <Form.Control type="password" placeholder="Senha" onChange={evt => this.updateInputValuePass(evt)}/>
                                    <span className="invalid-feedback d-block">
                                        {this.state.erropass}
                                    </span>
                                </Form.Group>                                     
                                <Button variant="primary" type="button" onClick={this.PostLoginData}>                                
                                    Entrar
                                </Button>
                            </Form>
                        </Col>            
                    </Row>     
                </Container>
            </div>              
        )
      }
      PostLoginData(event){
        this.setState({
            errologin: '',
            erropass: ''
        });
        if(this.state.login == undefined)
        {    
            this.setState({        
                errologin: 'Login obrigat贸rio'
            });            
        }
        else if(this.state.pass == undefined)
        {
            this.setState({        
                erropass: 'Senha obrigat贸ria'
            });            
        }
        else if(this.state.login.length == 0)        
        {            
            this.setState({        
                errologin: 'Login obrigat贸rio'
            });            
        }
        else if(this.state.pass.length == 0)        
        {
            this.setState({        
                erropass: 'Senha obrigat贸ria'
            });            
        }
        else
        {
            event.preventDefault();
            this.props.postlogin(this.state.login, this.state.pass );            
        }                
    } 
      updateInputValueLogin(evt) {          
        this.setState({
            login: evt.target.value
        });
      }
      updateInputValuePass(evt) {                
        this.setState({
            pass: evt.target.value
        });
      }      

}
    
function mapDispatchToProps(dispatch){
    return bindActionCreators({postlogin}, dispatch);
}

export default connect(null, mapDispatchToProps)(Login);
