import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Container, Row, Col } from "reactstrap";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import Select from "react-select";
import ListGroup from "react-bootstrap/ListGroup";
import DayPickerInput from "react-day-picker/DayPickerInput";
import CurrencyInput from "react-currency-input";

import { postModalCall } from "../actions/index";

const scaryAnimals = [
  { label: "Alligators", value: 1 },
  { label: "Crocodiles", value: 2 },
  { label: "Sharks", value: 3 },
  { label: "Small crocodiles", value: 4 },
  { label: "Smallest crocodiles", value: 5 },
  { label: "Snakes", value: 6 }
];

class IncluirMovimentacao extends Component {
  constructor(props) {
    super(props);
    this.state = { errologin: "" };
    this.modalCall = this.modalCall.bind(this);
  }
  state = {
    listitems: [
      { name: "shashank", age: 20 },
      { name: "vardan", age: 30 },
      { name: "somya", age: 40 }
    ],
    value: "",
    NumeroDocumento: "",
    amount: 0.0
  };
  getInitialState() {
    return { amount: "0.00" };
  }
  handleChange = event => {
    this.setState({ amount: event.target.value });
  };
  render() {
    return (
      <div>
        <h1>Incluir Movimentação</h1>
        <Container>
          <Row>
            <Col md={12}>
              <Card>
                <Card.Body>
                  <Row>
                    <Col md={3}>
                      <label>Matriz Contábil</label>
                      <div className="container">
                        <Select options={scaryAnimals} />
                      </div>
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={3}>
                      <label>Filial</label>
                      <div className="container">
                        <Select options={scaryAnimals} />
                      </div>
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={3}>
                      <label>Conta Corrente</label>
                      <div className="container">
                        <Select options={scaryAnimals} />
                      </div>
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={3}>
                      <label>Conta Despesa</label>
                      <div className="container">
                        <Select options={scaryAnimals} />
                      </div>
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                  </Row>
                  <Row>
                    <Col md={6}>
                      <label>Fornecedor</label>
                      <div className="container">
                        <Select options={scaryAnimals} />
                      </div>
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={6}>
                      <label>Operação</label>
                      <div className="container">
                        <Select options={scaryAnimals} />
                      </div>
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                  </Row>
                  <Row>
                    <Col md={3}>
                      <label>Data de emissão</label>
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={3}>
                      <label>Data de emissão</label>
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={3}>
                      <label>Data de emissão</label>
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={3}>
                      <label>Data de emissão</label>
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <DayPickerInput onDayChange={day => console.log(day)} />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                  </Row>
                  <Row>
                    <Col md={2}>
                      <label>Valor Original</label>
                      <CurrencyInput
                        value={this.state.amount}
                        onChangeEvent={this.handleChange}
                      />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={2}>
                      <label>Valor Baixado</label>
                      <CurrencyInput
                        value={this.state.amount}
                        onChangeEvent={this.handleChange}
                      />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={2}>
                      <label>Valor Contrapartida</label>
                      <CurrencyInput
                        value={this.state.amount}
                        onChangeEvent={this.handleChange}
                      />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={2}>
                      <label>Valor Caixa corrente</label>
                      <CurrencyInput
                        value={this.state.amount}
                        onChangeEvent={this.handleChange}
                      />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                    <Col md={3}>
                      <label>Valor Receita despesa</label>
                      <CurrencyInput
                        value={this.state.amount}
                        onChangeEvent={this.handleChange}
                      />
                      <span className="invalid-feedback d-block">
                        {this.state.errologin}
                      </span>
                    </Col>
                  </Row>
                  <hr></hr>
                  <Row>
                    <Col md={12}>
                      <Button variant="secondary">Secondary</Button>
                      <Button variant="primary" onClick={this.modalCall}>
                        Go somewhere
                      </Button>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }

  modalCall(event) {
    event.preventDefault();
    this.props.postModalCall();
  }
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({ postModalCall }, dispatch);
}
export default connect(null, mapDispatchToProps)(IncluirMovimentacao);
