import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Container, Row, Col } from "reactstrap";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Nav from "react-bootstrap/Nav";
import NavDropdown from "react-bootstrap/NavDropdown";
import Dashboard from "./Dashboard";
import IncluirMovimentacao from "./IncluirMovimentacao";
import ConsultarMovimentacao from "./ConsultarMovimentacao";
import logo from "../Resources/burgerMenu.png";
import { Tema1_2_Menu, Icon } from "../styles/css/Geral";


import { Theme } from "../styles/css/TESTE";
import { clickTESTE } from "../actions/TESTEAction";


class Home extends Component {
  constructor(props) {
    super(props);
    this.redirect = this.redirect.bind(this);

    this.PostclickTESTE = this.PostclickTESTE.bind(this);
  }
  state = {
    themeMode: "light",    
  };
  redirect(name) {
    window.location.href = name;
  }  
  ChangeTheme(id) {
    this.setState({ themeMode: id });    
  }
  render() {
    const { newValue } = this.props;

    return (
      <div>
        <Tema1_2_Menu>
          <Container>
            <Row>
              <Col md={12}>
                <Nav variant="pills">
                  <Icon>
                    <img src={logo} alt="logo" />
                  </Icon>
                  <NavDropdown title="" id="nav-dropdown">



                    {/* <Theme modeID={this.state.themeMode}>                  
                     <h1>Hello World</h1>
                     <p>Lorem Ipsum</p>                    
                    </Theme> */}
<h1 onClick={this.PostclickTESTE}>{newValue}</h1>

                    
                    <NavDropdown.Item
                      eventKey="1.1"
                      onClick={() => this.redirect("/")}
                    >
                      Dashboard
                    </NavDropdown.Item>
                    <NavDropdown.Item
                      eventKey="1.2"
                      onClick={() => this.redirect("/IncluirMovimentacao")}
                    >
                      Incluir movimentação
                    </NavDropdown.Item>
                    <NavDropdown.Item
                      eventKey="1.3"
                      onClick={() => this.redirect("/ConsultarMovimentacao")}
                    >
                      Consultar movimentação
                    </NavDropdown.Item>
                    <hr></hr>
                    <NavDropdown.Item
                      eventKey="2.1"
                      onClick={() => this.ChangeTheme("tema1_2")}
                    >
                      Tema 1
                    </NavDropdown.Item>
                    <NavDropdown.Item
                      eventKey="2.2"
                      onClick={() => this.ChangeTheme("tema1_11")}
                    >
                      Tema 2
                    </NavDropdown.Item>
                  </NavDropdown>
                </Nav>
              </Col>
            </Row>
          </Container>
        </Tema1_2_Menu>
        <Router>
          <Switch>
            <Route exact path="/">
              <Dashboard />
            </Route>
            <Route exact path="/IncluirMovimentacao">
              <IncluirMovimentacao />
            </Route>
            <Route exact path="/ConsultarMovimentacao">
              <ConsultarMovimentacao />
            </Route>
          </Switch>
        </Router>
      </div>
    );
  }
  PostclickTESTE(event){
    console.log(event);

    event.preventDefault();
    this.props.clickTESTE("HAMILTON");

  }
}
// function mapDispatchToProps(dispatch) {
//   return bindActionCreators({}, dispatch);
// }
// export default connect(null, mapDispatchToProps)(Home);


const mapStateToProps = store => ({
  newValue: store.clickState.newValue
});
export default connect(mapStateToProps)(Home);
