import React, {Component}  from 'react'
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import { Container, Row, Col} from 'reactstrap'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import {postlogin} from '../actions/index';

class Login extends Component{

    constructor(props){
        super(props);    
        this.PostLoginData = this.PostLoginData.bind(this);        
        this.state = {login: ''};
        this.state = {pass: ''};
        // this.state = {errologin: 'aaaaaaaaaaaaaaa'};
        // this.state = {erropass: ''};

        this.setState({
            errologin: 'aaaaaaaaaaaaaaa',
            erropass: ''
          });
    }
    
    PostLoginData(event){

        console.log("--------------------------");
        console.log(this.state.login);

        // if(this.state.login.length > 0 || this.state.login == undefined)
        // if(this.state.login == undefined)
        // {            
        //     this.state.errologin = 'Login obrigatório';
        //     return;
        // }
        // if(this.state.pass.length > 0 || this.state.pass == undefined)
        // if(this.state.pass == undefined)
        // {
        //     this.state.erropass = 'Senha obrigatória';
        //     return;
        // }        
        event.preventDefault();
        this.props.postlogin(this.state.login, this.state.pass );  
        this.state.errologin = '';
        this.state.erropass = '';
    } 
    
    render(){
        return(
            <div>      
                <Container>
                    <Row>
                        <Col xl={12}>
                            <h1>Login</h1>
                        </Col>            
                    </Row>    
                    <Row>
                        <Col xl={12}>
                            <Form>
                                <Form.Group controlId="formUsuario">                        
                                    <Form.Control type="text" placeholder="Usuário" onChange={evt => this.updateInputValueLogin(evt)}/>                        
                                </Form.Group>
                                <div className="invalid-feedback d-block">
                                    {this.state.errologin}
                                </div>
                                <Form.Group controlId="formPassword">                        
                                    <Form.Control type="password" placeholder="Senha" onChange={evt => this.updateInputValuePass(evt)}/>
                                </Form.Group>     
                                <div className="invalid-feedback d-block">
                                    {this.state.erropass}
                                </div>
                                <Button variant="primary" type="button" onClick={this.PostLoginData}>                                
                                    Entrar
                                </Button>
                            </Form>
                        </Col>            
                    </Row>     
                </Container>
            </div>  
        )
      }
      updateInputValueLogin(evt) {          
        this.setState({
            login: evt.target.value
        });
      }
      updateInputValuePass(evt) {        
        this.setState({
            pass: evt.target.value
        });
      }      

}
    
function mapDispatchToProps(dispatch){
    return bindActionCreators({postlogin}, dispatch);
}

export default connect(null, mapDispatchToProps)(Login);
